    private void cadastrarPBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cadastrarPBtnActionPerformed
        Produto NProduto = new Produto();
        NProduto.setNome(nomePCx.getText());
        NProduto.setPreco(Float.parseFloat(precoCx.getText()));
        NProduto.setQuantidade(Integer.parseInt(qteCx.getText()));       
        vetorProdutos[qteProdutos] = NProduto;
        JanelaPrincipal.lProdutos.addElement(NProduto.infos());
        JanelaPrincipal.listaProdutos.setModel(JanelaPrincipal.lProdutos);
        
        qteProdutos++;

    }//GEN-LAST:event_cadastrarPBtnActionPerformed

